import React, { useEffect, useState } from "react";
import './LNPage.css';
import {useLocation} from 'react-router-dom';

const LNPage = () => {
   const location = useLocation();

   const id = parseInt(location.pathname.replace( /^\D+/g, ''));

   const [LnInfo, setLnInfo] = useState([]);
   
   const anilist = require('anilist-node');
   const Anilist = new anilist();

   async function processData() {
      const data = await Anilist.media.manga(id);
      setLightNovels(data);
    }

   useEffect(() => {
      processData();
   }, []);

   return (
      <div className='lightnovel-page'>
         <div className='lightnovel-title'>
            <h1>{LnInfo.title.english}</h1>
         </div>
      </div>
   )
}

export default LNPage;
