import React from "react";
import './NovelInfo.css'

const NovelInfo = ({lninfos}) => {
    return (
        <div className='Ln-info'>
            <div className="title">
               <h3>{lninfos.title.english}</h3>
               <h3>{lninfos.title.romanji}</h3>
               <h3>{lninfos.title.native}</h3>

               <h3>{lninfos.genres}</h3>
               <h3>{lninfos.description}</h3>
               <h3>{lninfos.status}</h3>
               <h3>{lninfos.endDate.year !== null ? lninfos.endDate.year : 'Still releasing'}</h3> {/* need to put releasing/null if still releasing */}
               <h3>{lninfos.startDate.year}</h3>
               <h3>{lninfos.volumes !== null ? lninfos.volumes : 'Still releasing'}</h3>  {/* need to put releasing/null if still releasing */}
               <h3>{lninfos.siteUrl}</h3>
               <h3>{lninfos.genres}</h3>
               <h3>{lninfos.coverImage.large}</h3>
               <h1>tomato</h1>
            </div>
        </div>
    )
}

export default NovelInfo;
