import React, { useEffect, useState } from "react";
import './LNPage.css';
import titleinfo from "./titleinfo.jsx";
import {useLocation} from 'react-router-dom';

const LNPage = () => {
   const location = useLocation();

   const id = parseInt(location.pathname.replace( /^\D+/g, ''));

   const [LnInfo, setLnInfo] = useState([]);
   
   const anilist = require('anilist-node');
   const Anilist = new anilist();

   const processData = async () => {
      let info = []
      const data = await Anilist.media.manga(id);
      info.push(data);
      setLnInfo(info)
    }
    console.log(LnInfo)
   useEffect(() => {
      processData();
   }, []);

   return (      
      <div className='lightnovel-page'>

         <div className='lightnovel-title'>
            <h2>Hello</h2>
         </div>

         <div className="container">
               {LnInfo.map((LnInfos) => (
               <NovelWorld lightNovel={lightNovel} />
               ))}
         </div>



      </div>
   )
}

export default LNPage;
